import styled from 'styled-components';

export const Wrap = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    width: 400px;
    background: rgb(248,248,255);
    padding: 20px;
    border-radius: 20px;
    border: 6px solid rgba(0, 0, 0, 0.3);
    box-shadow: 20px 20px 0 rgba(0, 0, 0, 0.3);`
