import PropTypes from 'prop-types';
import { Component } from 'react';

export class Feedback extends Component {
  state = {
    good: 1,
    neutral: 0,
    bad: 0,
  };

  handleGood = () => {
    this.setState(prevState => {
      return { good: prevState.value + 1 };
    });
  };

  handleNetural = () => {};

  handleBad = () => {};

  countTotalFeedback = () => {};

  countPositiveFeedbackPercentage = () => {};

  render() {
    return (
      <div className="main_container">
        <h1 className="feedback_titl">Please leave feedback</h1>
        <div className="btn_container">
          <button className="btn" onClick={this.handleGood}>
            Good
          </button>
          <button className="btn" onClick={this.handleNetural}>
            Netural
          </button>
          <button className="btn" onClick={this.handleBad}>
            Bad
          </button>
        </div>
        <h2 className="statistic_titl">Statistics</h2>
        <p className="statistic_text">
          Neutral:
          <span className="statistic_result">{this.state.neutral}</span>
        </p>
        <p className="statistic_text">
          Good: <span className="statistic_result">{this.state.good}</span>
        </p>
        <p className="statistic_text">
          Bad: <span className="statistic_result">{this.state.bad}</span>
        </p>
        <p className="statistic_text">
          Total: <span className="statistic_result">0</span>
        </p>
        <p className="statistic_text">
          Positive feedback: <span className="statistic_result">0</span>
        </p>
      </div>
    );
  }
}
