import PropTypes from 'prop-types';
import { Component } from 'react';

export class Feedback extends Component {
  state = {
    good: 0,
    neutral: 0,
    bad: 0,
  };

  handleGood = () => {
    this.setState(prevState => ({
      good: prevState.good + 1,
    }));
  };

  handleNetural = () => {
    this.setState(prevState => ({
      neutral: prevState.neutral + 1,
    }));
  };

  handleBad = () => {
    this.setState(prevState => ({
      bad: prevState.bad + 1,
    }));
  };

  countTotalFeedback = () => {
    const { good, neutral, bad } = this.state;
    const total = good + neutral + bad;
    return total;
  };

  countPositiveFeedbackPercentage = () => {
    const good = this.state;
    const total = this.countTotalFeedback();
    const percentage = (good * 100) / total;
    return Math.round(percentage);
  };

  render() {
    const { good, neutral, bad } = this.state;
    const total = this.countTotalFeedback();
    const percentage = this.countPositiveFeedbackPercentage();

    return (
      <div className="main_container">
        <h1 className="feedback_title">Please leave feedback</h1>
        <div className="btn_container">
          <button className="btn" onClick={this.handleGood}>
            Good
          </button>
          <button className="btn" onClick={this.handleNetural}>
            Netural
          </button>
          <button className="btn" onClick={this.handleBad}>
            Bad
          </button>
        </div>
        <h2 className="statistic_title">Statistics</h2>
        <p className="statistic_text">
          Neutral: <span className="statistic_result">{neutral}</span>
        </p>
        <p className="statistic_text">
          Good: <span className="statistic_result">{good}</span>
        </p>
        <p className="statistic_text">
          Bad: <span className="statistic_result">{bad}</span>
        </p>
        <p className="statistic_text">
          Total: <span className="statistic_result">{total}</span>
        </p>
        <p className="statistic_text">
          Positive feedback:{' '}
          <span className="statistic_result">{percentage}</span>
        </p>
      </div>
    );
  }
}

